import React from 'react';
import { Link } from "react-router-dom";
import { useQuery, gql } from "@apollo/client";

const CATEGORIES = gql`
	query Getcategories {
		categories {
			name,
			id
		}
	}
`

const GLOBAL = gql`
query GetGlobal {
	global {
		id,
			navigation {
			links {
				id,
					href,
					label,
					target
			},
			leftButton {
				id,
					href,
					label,
					target
			},
			rightButton {
				id,
					href,
					label,
					target
			}
		}
	}
}
`

function SiteHeader() {
	const { loadingCat, errorCat, dataCat } = useQuery(CATEGORIES);


	if (loading1) return <p>Loading...</p>
	if (error1) return <p>Error :(</p>
	console.log(data1)

	return (
		<div className="site-header">
			<nav>

			</nav>
			<Link to="/"><h1>Strapi React</h1></Link>
			<nav className="categories">
				<span>Filtrer par cat√©gorie:</span>
				{dataCat.categories.map(category => (
					<Link key={category.id} to={`/categories/${category.id}`}>{category.name}</Link>
				))}
			</nav>
		</div>
	);
}

export default SiteHeader;
