import React from 'react';
import { Link } from "react-router-dom";
import { useQuery, gql } from "@apollo/client";

const POSTS = gql`
	query GetPosts {
		posts (sort: "created_at:desc") {
			title,
			postContent,
			id,
			thumbnail {
				url
			}
		},
		pages {
      		id,
    		title,
      		blocks {
        		... on ComponentBlockHero {
          			texte,
      				buttons {
						id,
						href,
						label,
						target
					}
        		}
      		}
    	}
	}
`

function Homepage() {
	const { loading, error, data } = useQuery(POSTS)

	if (loading) return <p>Loading...</p>
	if (error) return <p>Error :(</p>
	console.log(data)

	return (
		<>
		<section>
			<div className="container">
				<h1>{data.pages[0].blocks[0].texte}</h1>
				<Link to={`${data.pages[0].blocks[0].buttons[0].href}`}>{data.pages[0].blocks[0].buttons[0].label}</Link>
			</div>
		</section>
		<section className="">
			<div className="container">
				<div className="row">
					<h2>Nos Derniers Articles</h2>
					{data.posts.map(post => (
						<div key={post.id} className="col">
							<div className="card">
								<div className="card-body">
									<img src={`http://localhost:1337${post.thumbnail.url}`} className="card-img-top" alt="..."/>
									<h5 className="card-title">{post.title}</h5>
									<p className="card-text">{post.postContent}</p>
									<Link to={`/blog/${post.id}`}>Lire plus</Link>
								</div>
							</div>
						</div>
					))}
				</div>
			</div>
		</section>
		</>
	);
}

export default Homepage;
